mapper.delete:
DELETE FROM member 
WHERE member_id = #{memberId} 
AND	  member_pw = #{memberPw}

mapper.select:
SELECT
	CONCAT(SUBSTRING(member_id ,1,3),'****')  
FROM member
WHERE member_name = #{memberName}
AND member_phone = #{memberPhone}
AND member_email = #{memberEmail}

mapper.update:
UPDATE member 
SET	member_pw = #{memberPw}
WHERE member_id = #{memberId}
AND member_email = #{memberEmail}
